import { useState } from "react";

const DiaryEditor = () => {
  // 동작이 비슷한 state는 하나로 묶는다.
  const [state, setState] = useState({
    author: "",
    content: "",
  })

  return (<div className="DiaryEditor">
  <h2>오늘의 일기</h2>
  <div>
    <input 
    name="author"
    value={state.author} 
    onChange={(e) => { // 인풋 값이 바뀔때 수행하는 이벤트 
      setState({
        author: e.target.value,
        content: state.content,
      });
    }}
    />
  </div>
  <div>
  <textarea 
  vlaue={state.content} 
  onChange={(e) => {
    setState({ // 상태를 변화시킬땐 상태변환함수에 새로운 변화시킬 값을 인자로 전달. 
      author: e.target.value,
      content: state.content,
    })
  }}
  />
  </div>
  </div>
  );
};

export default DiaryEditor;
