    // });
  };

  return (
    <div className="DiaryEditor">
      <h2>üßÄ Ïò§ÎäòÏùò Í∏∞Î∂Ñ üßÄ </h2>
      <div>
        <input
          ref={authorInput}
          value={state.author}
          onChange={handleChangeState}
